INSERT INTO dialogue_content (content_id, record, `order`, turn_id) VALUES('20220504DS000025', 'In 2021, the company achieved operating income of 758.67 billion yuan, +5.1% year-on-year, realized net profit of 23.81 billion yuan, +23.6% year-on-year, and realized net profit of 21.67 billion yuan, +58.2% year-on-year', 2, 1);
