INSERT INTO dialogue_content (content_id, record, `order`, turn_id) VALUES('20220514DS000035', 'In 21Q4, the company''s gross profit margin of total revenue and net profit margin of total revenue were 92.70%/50.16%, respectively, +0.67/-0.31pct year-on-year. Sales/management/R&D/finance expense ratio +1.14/-0.29/+0.07/+0.01pct, respectively', 19, 8);
