INSERT INTO dialogue_content (content_id, record, `order`, turn_id) VALUES('20220514DS000035', '22 years the company will build a strong product matrix; Create excellent channel ecology, focusing on self-support, business super e-commerce, group buying channels; Linkage of various forces to maintain good market order of Moutai wine', 22, 10);
